mapper.select:
		SELECT 	BNO, TITLE, CONTENT, WRITER, REGDATE, VIEWCNT
 		FROM 	TBL_BOARD
 		WHERE	BNO = #{bno}

mapper.update:
UPDATE TBL_BOARD
SET TITLE = #{title} , CONTENT = #{content}
WHERE 	BNO = #{bno}

 		UPDATE TBL_BOARD 
		SET VIEWCNT = VIEWCNT + 1
		WHERE BNO = #{bno}

mapper.select:
SELECT 	RSEQ, BNO, RWRITER,  RCONTENT 
FROM 	TBL_REPLY 
WHERE   BNO = #{bno}
